--------------------------------------------------------------------------------------------------------------------- Setup functions for this job.  Generally should not be modified.--------------------------------------------------------------------------------------------------------------------- Initialization function for this job file.function get_sets()    mote_include_version = 2    -- Load and initialize the include file.    include('Mote-Include.lua')end-- Setup vars that are user-independent.  state.Buff vars initialized here will automatically be tracked.function job_setup()    state.Buff.Sentinel = buffactive.sentinel or false    state.Buff.Cover = buffactive.cover or false    state.Buff.Doom = buffactive.Doom or falseend--------------------------------------------------------------------------------------------------------------------- User setup functions for this job.  Recommend that these be overridden in a sidecar file.--------------------------------------------------------------------------------------------------------------------- Setup vars that are user-dependent.  Can override this function in a sidecar file.function user_setup()    state.OffenseMode:options('Normal', 'Acc', 'DD','Breath','PDT','MDT')     state.WeaponskillMode:options('Normal', 'Acc')    state.CastingMode:options('Normal', 'Resistant')    state.PhysicalDefenseMode:options('PDT')    state.MagicalDefenseMode:options('MDT')    	    update_defense_mode()        select_default_macro_book()end-- Define sets and vars used by this job file.function init_gear_sets()    --------------------------------------    -- Precast sets    --------------------------------------		sets.Enmity = {ammo="Iron Gobbet",head="Hydra Salade",        neck="Ritter Gorget",hands="Hydra Moufles",ear1="Hades Earring +1",ear2="Hades Earring +1",        body="Hydra Haubert",ring1="Corneus Ring",ring2="Hercules' Ring", waist="Trance Belt",        back="Valor Cape"